import { Component, OnInit } from "@angular/core";
import { SellerService } from "../services/seller.service";
import { Router } from "@angular/router";
import { SignUp } from "../data-type";
@Component({
  selector: "app-seller-auth",
  templateUrl: "./seller-auth.component.html",
  styleUrls: ["./seller-auth.component.css"],
})
export class SellerAuthComponent implements OnInit {
  constructor(private seller: SellerService, private router: Router) {}
  showLogin = false;
  authError:string = '';
  ngOnInit(): void {
    this.seller.reloadSeller();
  }

  signUp(data: SignUp): void {
    // console.log(data);
    // this.seller.userSignUp(data).subscribe((result) => {
    //   if (result) {
    //     this.router.navigate(["seller-home"]);
    //   }
    // });

    this.seller.userSignUp(data);
  }

  login(data: SignUp): void {
    // console.log(data);
    this.seller.userLogin(data);
    this.seller.isLoginError.subscribe((isError) => {
      if (isError) {
        this.authError = "Email or Password is not Correct";
      }
    });
  }

  openLogin() {
    this.showLogin = true;
  }
  openSignUp() {
    this.showLogin = false;
  }
}
<div class="seller-auth">
  <!-- sign up -->
  <div *ngIf="!showLogin" class="sign-up">
    <h2>Seller Sign-up</h2>
    <form
      #sellerSignUp="ngForm"
      (ngSubmit)="signUp(sellerSignUp.value)"
      class="common-form"
    >
      <input
        class="form-input"
        type="text"
        placeholder="Enter Name"
        name="name"
        ngModel
      />
      <input
        class="form-input"
        type="password"
        placeholder="Enter Password"
        name="password"
        ngModel
      />
      <input
        class="form-input"
        type="text"
        placeholder="Enter Email"
        name="email"
        ngModel
      />
      <button class="form-button">Sign Up</button>
      <a class="auth-toggle-link" (click)="openLogin()"
        >Already Have Account? Click to Login</a
      >
    </form>
  </div>

  <!-- login -->
  <div *ngIf="showLogin" class="login">
    <h2>Seller Login</h2>
    <p>{{ authError }}</p>
    <form
      #sellerLogin="ngForm"
      (ngSubmit)="login(sellerLogin.value)"
      class="common-form"
    >
      <input
        class="form-input"
        type="text"
        placeholder="Enter Email"
        name="email"
        ngModel
      />
      <input
        class="form-input"
        type="password"
        placeholder="Enter Password"
        name="password"
        ngModel
      />

      <button class="form-button">Login</button>
      <a class="auth-toggle-link" (click)="openSignUp()"
        >Don't Have Account? Click to Sign-Up</a
      >
    </form>
  </div>
</div>
